content.css:
.knob-strip .knob-container {
right: 0;
left: auto;
border-radius: 37rem / 45rem;
background: var(--color-background);
}
.knob-strip .input-container {
  z-index: 0;
  --alpha-stroke: 0;
}

.text-container {
  --widget-padding: -1rem;
  --alpha-stroke: 0;
  --alpha-fill-off: 0.1;
}
.modal-container,
.button-container {
  --alpha-stroke: 0;
  --alpha-fill-off: 0.15;
  --widget-padding: -1rem;
}

.input-container {
  --alpha-stroke: 0; 
  --alpha-fill-off: 0.05;
}
.button-container.no-pulse inner:after {
  animation: none
}

.flat-btns .button-container {
--alpha-stroke:1;
--alpha-fill-off: 0;
--color-text: rgba(110,128,156,0.8);
--color-stroke: var(--color-text);
}

.flat-btns .button-container.on {
--alpha-stroke:1;
--alpha-fill-on: 0.2;
--color-text-on: var(--color-widget);
--color-stroke: var(--color-widget)
}

widgets.css:
label:after{display:none}
.popup {
