}
.popup-wrapper {
box-shadow: 0 0 50px 0 rgba(0,0,0,0.5);
border-radius:5px
}
.cfg-label {
min-width: 120rem!important;
}
.cfg-label inner {
padding-left:8rem;
}

inner {
padding: 16rem;
}
hr {
height: 8rem
}
p {
line-height: 20rem;
}
b {
font-weight: bold
}
* {
user-select:text;
}
.html {
overflow: auto;
text-align: left;
}

